import React, { Component } from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import './App.css';
import styled from 'styled-components';
import Crossword from "react-crossword";
// import Slider from "react-slider-game";
import HamburgerMenu from "react-hamburger-menu";
import crosswordData from "./components/data/crosswordLA"

// import tourStops from './components/data/tourStops.jsx';
// import mapVector from './components/mapVector';

import Iframe from 'react-iframe';
import anime from 'animejs/lib/anime.es.js';
import ReactTooltip from 'react-tooltip'
// import SlidingTilePuzzle from 'react-sliding-tile-puzzle';

// import Display from "./components/Display"
import mapVector from './components/mapVector';
import tourStops from './components/data/tourStops';


const Display = styled.div`

  height: 100vh;
  width: 100vw;
  background: url(img/background.jpg)

`

const Welcome = styled.div`

  .welcome-container {
    background-color: white;
    height: 400px;
    // width: 660px;
    padding: 50px;
    flex-direction: column;
    border-radius: 10px;
    border: 2px solid ${props => props.theme.pink};
    box-shadow: 0px 0px 20px rgba(0,0,0,.4);
  }

  .welcome-logo {
    background-color: lightgray;
    height: 200px;
    width: 200px;
  }

  .welcome-copy {
    margin-top: 30px;
    max-width: 400px;
    margin-bottom: 10px;

    p {
      margin: 0;
    }
  }

  .welcome-enterbutton {
    margin: 20px 20px 0 20px;
    background-color: ${props => props.theme.pink};
    border: 2px solid ${props => props.theme.pink};
    color: white;
    border-radius: 10px;
    padding: 10px 14px;
    cursor: pointer;
    transition: .2s ease;
  }

  .welcome-enterbutton:hover {
    background-color: white;
    color: ${props => props.theme.pink};
    margin-top: 15px;
    margin-bottom: 5px;
  }

`

const Merch = styled.div`

  .merch-container {
    background-color: white;
    height: 500px;
    // width: 500px;
    padding: 50px;
    flex-direction: column;
    border-radius: 10px;
    border: 2px solid ${props => props.theme.pink};
    box-shadow: 0px 0px 20px rgba(0,0,0,.4);
  }

  .merch-thumbnails-container {
    width: 660px;
    flex-wrap: wrap;
    max-height: 500px;
    overflow: scroll;
  }

  .merch-thumbnail {
    // background-color: pink;
    width: 200px;
    flex-direction: column;
    margin: 10px;
    padding-bottom: 20px;
    cursor: pointer;
  }

  .merch-thumbnail-picture {
    background-color: lightgray;
    height: 300px;
    width: 200px;
    margin-bottom: 10px;
  }

  h3, h4 {
    margin: 0;
  }

  .merch-enter-button {
    margin: 20px 20px 0 20px;
    background-color: ${props => props.theme.pink};
    border: 2px solid ${props => props.theme.pink};
    color: white;
    border-radius: 10px;
    padding: 10px 14px;
    cursor: pointer;
    transition: .2s ease;
  }

  .merch-enter-button:hover {
    background-color: white;
